    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Captura el texto del JTextField
        String searchValue = jTextField1.getText();

        // Conectar a la colección
        MongoCollection<Document> collection = MongoConnection.connect();

        if (collection != null) {
            // Crear consulta
            Document query = new Document("id", searchValue);
            Document cellPhone = collection.find(query).first();

            if (cellPhone != null) {
                // Mostrar el mensaje en un JOptionPane
                JOptionPane.showMessageDialog(this, "Se encontró celular", "Resultado de la Búsqueda", JOptionPane.INFORMATION_MESSAGE);
                // Abrir la nueva ventana con los detalles del celular
                new view_detailscellphone(searchValue).setVisible(true);
                dispose();
            } else {
                // Mostrar un mensaje de error si no se encuentra el celular
                JOptionPane.showMessageDialog(this, "No se encontró ningún celular con el ID: " + searchValue, "Error", JOptionPane.ERROR_MESSAGE);
            }
        } else {
            // Mostrar un mensaje de error si no se puede conectar a la base de datos
            JOptionPane.showMessageDialog(this, "Error al conectar con la base de datos.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

