    private void chargeData() {
        javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) tColorList.getModel();
        // Limpiar tabla
        model.setRowCount(0);

        // Obtener todos los colores de MongoDB
        List<Color> colors = MongoDBManager.getInstance().getAllColors();

        // Añadir a la tabla
        for (Color color : colors) {
            model.addRow(new Object[]{
                color.getColorId(),
                color.getColorName(),
                color.getType(),
                color.getPrice()
            });
        }
    }

    private void btnDeleteColorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteColorActionPerformed
        int selectedRow = tColorList.getSelectedRow();
        if (selectedRow != -1) {
            // Obtener el modelo de la tabla
            javax.swing.table.DefaultTableModel model = (javax.swing.table.DefaultTableModel) tColorList.getModel();

            // Obtener el ID del color (suponiendo que está en la columna 1)
            String colorId = (String) model.getValueAt(selectedRow, 1);

            // Confirmar eliminación
            int option = javax.swing.JOptionPane.showConfirmDialog(this,
                    "¿Estás seguro de eliminar este color?",
                    "Confirmar eliminación",
                    javax.swing.JOptionPane.YES_NO_OPTION);

            if (option == javax.swing.JOptionPane.YES_OPTION) {
                // Eliminar de MongoDB
                MongoDBManager.getInstance().deleteColor(colorId);

                // Eliminar la fila de la tabla para evitar recargar todos los datos
                model.removeRow(selectedRow);

                javax.swing.JOptionPane.showMessageDialog(this,
                        "Color eliminado correctamente",
                        "Éxito", javax.swing.JOptionPane.INFORMATION_MESSAGE);
            }
        } else {
            // Mostrar error si no se selecciona ninguna fila
            javax.swing.JOptionPane.showMessageDialog(this,
                    "Selecciona un color para eliminar",
                    "Error", javax.swing.JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnDeleteColorActionPerformed

