    private void savebtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_savebtnActionPerformed
    
        String id = txtid.getText();
    String monto = txtamount.getText();
    String tiempo = txttime.getText();
    
    // Validar que los campos no estén vacíos
    if (id.isEmpty() || monto.isEmpty() || tiempo.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Por favor, complete todos los campos.");
        return;
    }

    try {
        // Obtener la conexión a la base de datos
        MongoDatabase database = MongoDBUtils.getDatabase();
        MongoCollection<Document> collection = database.getCollection("inversiones");
        
        // Crear documento para guardar en MongoDB
        Document inversion = new Document("id", id)
                .append("monto", monto)
                .append("tiempo", tiempo)
                .append("tasaInteres", 7.6); // Tasa fija del 7.6%
        
        // Insertar en MongoDB
        collection.insertOne(inversion);
        
        JOptionPane.showMessageDialog(this, "Datos guardados con éxito.");
        
        // Limpiar los campos después de guardar
        txtid.setText("");
        txtamount.setText("");
        txttime.setText("");

    } catch (Exception ex) {
        JOptionPane.showMessageDialog(this, "Error al guardar: " + ex.getMessage());
    }     
    }//GEN-LAST:event_savebtnActionPerformed

