    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
   try {
        
        String country1 = txtCountry1.getText();  
        String country2 = txtCountry2.getText();
        String pib1Text = txtPIB1.getText();
        String pib2Text = txtPIB2.getText();

    
        if (country1.isEmpty() || country2.isEmpty() || pib1Text.isEmpty() || pib2Text.isEmpty()) {
            JOptionPane.showMessageDialog(rootPane, "Por favor ingrese todos los datos.");
            return; 
        }

        double gdp1 = 0;
        double gdp2 = 0;
        try {
            gdp1 = Double.parseDouble(pib1Text);  
            gdp2 = Double.parseDouble(pib2Text);
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(rootPane, "Por favor ingrese valores válidos para los PIB.");
            return;  
        }

        boolean success1 = PIBController.addCountryGDP(country1, gdp1);
        boolean success2 = PIBController.addCountryGDP(country2, gdp2);

        if (success1 && success2) {
            JOptionPane.showMessageDialog(rootPane, "Guardado correctamente el PIB de ambos países.");
        } else {
            JOptionPane.showMessageDialog(rootPane, "Error al guardar los datos del PIB.");
        }

  
        emptyFields();

    } catch (Exception e) {
        JOptionPane.showMessageDialog(rootPane, "Ocurrió un error inesperado: " + e.getMessage());
    }


    }//GEN-LAST:event_btnAddActionPerformed

