    private void btnCalculateDifferenceActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateDifferenceActionPerformed
        try {

            double pib1 = Double.parseDouble(txtPIB1.getText());
            double pib2 = Double.parseDouble(txtPIB2.getText());

            double difference = pib1 - pib2;

            txtDifference.setText(String.valueOf(difference));

        } catch (NumberFormatException e) {

            JOptionPane.showMessageDialog(rootPane, "Ingrese datos validos.");
        }
    }//GEN-LAST:event_btnCalculateDifferenceActionPerformed

private void emptyFields() {
 
    txtPIB1.setText("");  
    txtPIB2.setText("");  
    txtCountry1.setText(""); 
    txtCountry2.setText("");  
}


