    private void btnAddPetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddPetActionPerformed
         try {
        String petName = txtPetName.getText().trim();
        String petBreed = txtPetBreed.getText().trim();
        int petAge = Integer.parseInt(txtPetAge.getText().trim());
        float petWeight = Float.parseFloat(txtPetWeight.getText().trim());
        
        if (petName.isEmpty() || petBreed.isEmpty()) {
            JOptionPane.showMessageDialog(this, 
                    "Pet name and breed cannot be empty", 
                    "Input Error", 
                    JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        if (petAge < 0) {
            JOptionPane.showMessageDialog(this, 
                    "Age cannot be negative", 
                    "Input Error", 
                    JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        if (petWeight <= 0) {
            JOptionPane.showMessageDialog(this, 
                    "Weight must be greater than 0", 
                    "Input Error", 
                    JOptionPane.ERROR_MESSAGE);
            return;
        }
        
        Pet pet = new Pet(petName, petBreed, petAge, petWeight);
        
        boolean success = PetController.insertPet(pet);
        
        if (success) {
            JOptionPane.showMessageDialog(this, 
                    "Pet added successfully!", 
                    "Success", 
                    JOptionPane.INFORMATION_MESSAGE);
            
            clearForm();
        } else {
            JOptionPane.showMessageDialog(this, 
                    "Failed to add pet", 
                    "Error", 
                    JOptionPane.ERROR_MESSAGE);
        }
    } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(this, 
                "Please enter valid numbers for age and weight", 
                "Input Error", 
                JOptionPane.ERROR_MESSAGE);
    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, 
                "Error: " + e.getMessage(), 
                "Error", 
                JOptionPane.ERROR_MESSAGE);
    }

    }//GEN-LAST:event_btnAddPetActionPerformed
    private void clearForm() {
    txtPetName.setText("");
    txtPetBreed.setText("");
    txtPetAge.setText("");
    txtPetWeight.setText("");
    txtPetName.requestFocus(); 
}
